import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ListarProduto.css';

const ListarProdutos = () => {
  const [produtos, setProdutos] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchProdutos();
  }, []);

  const fetchProdutos = async () => {
    try {
      const [produtosResponse, categoriasResponse] = await Promise.all([
        axios.get('http://localhost:4003/produtos'),
        axios.get('http://localhost:4003/categorias'),
      ]);

      const produtosData = produtosResponse.data;
      const categoriasData = categoriasResponse.data;

      const produtosComCategoria = produtosData.map(produto => {
        const categoria = categoriasData.find(categoria => categoria.id === produto.categoriaId);
        return {
          ...produto,
          categoria: categoria ? categoria.nome : ''
        };
      });

      setProdutos(produtosComCategoria);
    } catch (error) {
      console.error(error);
    }
  };

  const handleVoltar = () => {
    navigate('/cadastroProduto');
  };

  const navigate = useNavigate();

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4003/produto/${id}`);
      fetchProdutos();
    } catch (error) {
      console.error(error);
    }
  };

  const handleInputChange = (e, id) => {
    const { name, value } = e.target;
    setProdutos((prevState) =>
      prevState.map((produto) =>
        produto.id === id ? { ...produto, [name]: value } : produto
      )
    );
  };

  const handleSalvar = async (id) => {
    try {
      const produto = produtos.find((produto) => produto.id === id);
      const { nome, descricao, categoriaId } = produto; // Extrair apenas as propriedades necessárias

      await axios.put(`http://localhost:4003/produto/${id}`, { nome, descricao, categoriaId }); // Enviar nome, descricao e categoriaId
      setEditingId(null);
      fetchProdutos(); // Atualizar os dados da lista após salvar a alteração
    } catch (error) {
      console.error(error);
    }
  };

  const handleCancelarEdicao = () => {
    setEditingId(null);
  };

  const handleEditar = (id) => {
    setEditingId(id);
  };

  return (
    <div className="container-lista">
      <h1>Lista de Produtos</h1>
      <table className="table">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Descrição</th>
            <th>Categoria</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {produtos.map((produto) => (
            <tr key={produto.id}>
              <td>
                {editingId === produto.id ? (
                  <input
                    type="text"
                    name="nome"
                    value={produto.nome}
                    onChange={(e) => handleInputChange(e, produto.id)}
                  />
                ) : (
                  produto.nome
                )}
              </td>
              <td>
                {editingId === produto.id ? (
                  <input
                    type="text"
                    name="descricao"
                    value={produto.descricao}
                    onChange={(e) => handleInputChange(e, produto.id)}
                  />
                ) : (
                  produto.descricao
                )}
              </td>
              <td>
                {editingId === produto.id ? (
                  <input
                    type="text"
                    name="categoriaId"
                    value={produto.categoriaId}
                    onChange={(e) => handleInputChange(e, produto.id)}
                  />
                ) : (
                  produto.categoria
                )}
              </td>
              <td>
                {editingId === produto.id ? (
                  <React.Fragment>
                    <button className="button" onClick={() => handleSalvar(produto.id)}>
                      Salvar
                    </button>
                    <button className="button button-space" onClick={() => handleCancelarEdicao()}>
                      Cancelar
                    </button>
                  </React.Fragment>
                ) : (
                  <React.Fragment>
                    <button className="button" onClick={() => handleEditar(produto.id)}>
                      Alterar
                    </button>
                    <button className="button button-space" onClick={() => handleDelete(produto.id)}>
                      Deletar
                    </button>
                  </React.Fragment>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <button type="button" className="buttonVoltar" onClick={handleVoltar}>
          Voltar
        </button>
      </div>
    </div>
  );
};

export default ListarProdutos;
