import React, { useState, useEffect } from 'react';
import axios from 'axios';
import * as Yup from 'yup';

function CategoriaProduto() {
  const [categorias, setCategorias] = useState([]);
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [categoriaSelecionada, setCategoriaSelecionada] = useState(null);

  const schema = Yup.object().shape({
    nome: Yup.string().required('O nome da categoria é obrigatório'),
    descricao: Yup.string().required('A descrição da categoria é obrigatória'),
  });

  const fetchCategorias = async () => {
    try {
      const response = await axios.get('http://localhost:4003/categorias');
      setCategorias(response.data);
    } catch (error) {
      console.error(error);
      setErrorMessage('Ocorreu um erro ao obter as categorias');
    }
  };

  useEffect(() => {
    fetchCategorias();
  }, []);

  const handleCreateCategoria = async (e) => {
    e.preventDefault();

    try {
      await schema.validate({ nome, descricao }, { abortEarly: false });

      if (categoriaSelecionada === null) {
        // Criação de uma nova categoria
        const response = await axios.post('http://localhost:4003/categoria', {
          nome,
          descricao,
        });

        const novaCategoria = response.data;
        setCategorias([...categorias, novaCategoria]);
        setSuccessMessage('Categoria criada com sucesso!');
      } else {
        // Atualização de uma categoria existente
        const response = await axios.put(
          `http://localhost:4003/categorias/${categoriaSelecionada.id}`,
          {
            nome,
            descricao,
          }
        );

        const categoriaAtualizada = response.data;
        const novasCategorias = categorias.map((categoria) => {
          if (categoria.id === categoriaAtualizada.id) {
            return categoriaAtualizada;
          }
          return categoria;
        });

        setCategorias(novasCategorias);
        setSuccessMessage('Categoria atualizada com sucesso!');
        setCategoriaSelecionada(null); // Limpar a categoria selecionada
      }

      setErrorMessage('');

      setNome('');
      setDescricao('');

      setErrors({}); // Limpar os erros
    } catch (error) {
      if (error instanceof Yup.ValidationError) {
        const validationErrors = {};
        error.inner.forEach((e) => {
          validationErrors[e.path] = e.message;
        });
        setErrors(validationErrors);
      } else {
        console.error(error);
        setErrorMessage(
          'Ocorreu um erro ao criar/atualizar a categoria. Por favor, tente novamente.'
        );
        setSuccessMessage('');
      }
    }
  };

  const handleDeleteCategoria = async (categoria) => {
    try {
      await axios.delete(`http://localhost:4003/categorias/${categoria.id}`);
      setCategorias(categorias.filter((c) => c.id !== categoria.id));
      setSuccessMessage('Categoria excluída com sucesso!');
      setErrorMessage('');
    } catch (error) {
      console.error(error);
      setErrorMessage('Ocorreu um erro ao excluir a categoria');
      setSuccessMessage('');
    }
  };

  const handleEditCategoria = (categoria) => {
    setCategoriaSelecionada(categoria);
    setNome(categoria.nome);
    setDescricao(categoria.descricao);
    setErrors({});
    setSuccessMessage('');
    setErrorMessage('');
  };

  const handleCancelEdit = () => {
    setCategoriaSelecionada(null);
    setNome('');
    setDescricao('');
    setErrors({});
    setSuccessMessage('');
    setErrorMessage('');
  };

  return (
    <div>
      <h2>Cadastro de Categorias</h2>
      <form onSubmit={handleCreateCategoria}>
        <div>
          <label>Nome:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
          />
          {errors.nome && <span className="input-error">{errors.nome}</span>}
        </div>
        <div>
          <label>Descrição:</label>
          <input
            type="text"
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
          />
          {errors.descricao && (
            <span className="input-error">{errors.descricao}</span>
          )}
        </div>
        {successMessage && (
          <div className="success-message">{successMessage}</div>
        )}
        {errorMessage && <div className="error-message">{errorMessage}</div>}
        {categoriaSelecionada ? (
          <React.Fragment>
            <button type="submit">Atualizar Categoria</button>
            <button type="button" onClick={handleCancelEdit}>
              Cancelar Edição
            </button>
          </React.Fragment>
        ) : (
          <button type="submit">Criar Categoria</button>
        )}
      </form>

      <h2>Lista de Categorias</h2>
      <ul>
        {categorias.map((categoria) => (
          <li key={categoria.id}>
            {categoria.nome} - {categoria.descricao}
            <button onClick={() => handleEditCategoria(categoria)}>
              Editar
            </button>
            <button onClick={() => handleDeleteCategoria(categoria)}>
              Excluir
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CategoriaProduto;
