import React, { useState, useEffect } from 'react';
import axios from 'axios';
import * as Yup from 'yup';
import './'


function CadastroProduto() {
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');
  const [categoriaId, setCategoriaId] = useState('');
  const [categorias, setCategorias] = useState([]);
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const schema = Yup.object().shape({
    nome: Yup.string().required('O nome do produto é obrigatório'),
    descricao: Yup.string().required('A descrição do produto é obrigatória'),
    categoriaId: Yup.string().required('Selecione uma categoria'),
  });

  useEffect(() => {
    fetchCategorias();
  }, []);

  const fetchCategorias = async () => {
    try {
      const response = await axios.get('http://localhost:4003/categoria');
      setCategorias(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCreateProduto = async (e) => {
    e.preventDefault();

    try {
      await schema.validate(
        { nome, descricao, categoriaId },
        { abortEarly: false }
      );

      await axios.post('http://localhost:4003/produto', {
        nome,
        descricao,
        categoriaId,
      });

      setSuccessMessage('Produto criado com sucesso!');
      setErrorMessage('');

      setNome('');
      setDescricao('');
      setCategoriaId('');

      setErrors({}); // Limpar os erros
    } catch (error) {
      if (error instanceof Yup.ValidationError) {
        const validationErrors = {};
        error.inner.forEach((e) => {
          validationErrors[e.path] = e.message;
        });
        setErrors(validationErrors);
      } else {
        console.error(error);
        setErrorMessage(
          'Ocorreu um erro ao criar o produto. Por favor, tente novamente.'
        );
        setSuccessMessage('');
      }
    }
  };

  return (
    <div>
      <h2>Cadastro de Produto</h2>
      <form onSubmit={handleCreateProduto}>
        <div>
          <label>Nome:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
          />
          {errors.nome && <span className="input-error">{errors.nome}</span>}
        </div>
        <div>
          <label>Descrição:</label>
          <input
            type="text"
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
          />
          {errors.descricao && (
            <span className="input-error">{errors.descricao}</span>
          )}
        </div>
        <div>
          <label>Categoria:</label>
          <select
            value={categoriaId}
            onChange={(e) => setCategoriaId(e.target.value)}
          >
            <option value="">Selecione uma categoria</option>
            {categorias.map((categoria) => (
              <option key={categoria.id} value={categoria.id}>
                {categoria.nome}
              </option>
            ))}
          </select>
          {errors.categoriaId && (
            <span className="input-error">{errors.categoriaId}</span>
          )}
        </div>
        {successMessage && (
          <div className="success-message">{successMessage}</div>
        )}
        {errorMessage && <div className="error-message">{errorMessage}</div>}
        <button type="submit">Criar Produto</button>
      </form>
    </div>
  );
}

export default CadastroProduto;
